package com.example.npvcalc.entity; import jakarta.persistence.*; import java.time.*; @Entity @Table(name="user_tokens", indexes=@Index(name="idx_token", columnList="token")) public class UserToken { @Id @GeneratedValue(strategy=GenerationType.IDENTITY) private Long id; @Column(nullable=false,length=128) private String username; @Column(nullable=false,unique=true,length=128) private String token; @Column(name="expires_at",nullable=false) private OffsetDateTime expiresAt; @Column(name="revoked",nullable=false) private boolean revoked=false; public Long getId(){return id;} public void setId(Long id){this.id=id;} public String getUsername(){return username;} public void setUsername(String u){this.username=u;} public String getToken(){return token;} public void setToken(String t){this.token=t;} public OffsetDateTime getExpiresAt(){return expiresAt;} public void setExpiresAt(OffsetDateTime e){this.expiresAt=e;} public boolean isRevoked(){return revoked;} public void setRevoked(boolean r){this.revoked=r;} }