package com.example.npvcalc.service; import com.example.npvcalc.entity.*; import com.example.npvcalc.repository.*; import org.springframework.stereotype.*; import java.time.*; import java.util.*; @Service public class NpvService { private final NpvRepository repo; public NpvService(NpvRepository r){this.repo=r;} public Optional<NpvValue> findStored(LocalDate date,String symbol){ return repo.findByValuationDateAndSymbol(date,symbol);} }